        setEmail(event.target.value);
    }

    const onPhoneChangeHandler = (event) => {
        setPhone(event.target.value);
    }

    return(
        <Grid item container direction="row">
            <Grid item container direction="column" justify="center" lg={3}>
                <Grid item>
                    <Typography variant="h2" style={{lineHeight: 1}}>Contact Us</Typography>
                    <Typography variant="body1" style={{color: theme.palette.common.blue}}>We're waiting.</Typography>
                </Grid>
                <Grid item container>
                    <Grid item>
                        <img src={phoneIcon} alt="phone"style={{marginRight: '0.5em'}}/>
                    </Grid>
                    <Grid item>
                        <Typography variant="body1" style={{color: theme.palette.common.blue, fontSize: '1rem'}}>(555) 555-5555</Typography>
                    </Grid>
                </Grid>
                <Grid item container>
                    <Grid item>
                        <img src={emailIcon} alt="email"style={{marginRight: '0.5em', verticalAlign: "bottom"}}/>
                    </Grid>
                    <Grid item>
                        <Typography variant="body1" style={{color: theme.palette.common.blue, fontSize: '1rem'}}>tranminhtri9090@gmail.com</Typography>
                    </Grid>
                </Grid>
                <Grid item container>
                    <Grid item>
                        <TextField label="Name" id="Name" value={name} onChange={(e) => onNameChangeHandler(e)}/>
                    </Grid>
                    <Grid item>
                        <TextField label="Email" id="email" value={email} onChange={(e) => onEmailChangeHandler(e)}/>
                    </Grid>
                    <Grid item>
                        <TextField label="Phone" id="phone" value={phone} onChange={(e) => onPhoneChangeHandler(e)}/>
                    </Grid>
                </Grid>
            </Grid>
            <Grid item container className={classes.background} lg={9}>

            </Grid>
        </Grid>
    );
}
