import React, {useState} from 'react';
import {Link} from 'react-router-dom';
import {makeStyles, useTheme} from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import ButtonArrow from '../original';
import useMediaQuery from '@material-ui/core/useMediaQuery';

import background from '../assets/background.jpg';
import phoneIcon from '../assets/phone.svg';
import emailIcon from '../assets/email.svg';
import airplane from '../assets/send.svg';

const useStyles = makeStyles(theme => ({
    background: {
        backgroundImage: `url(${background})`,
        backgroundPosition: 'center',
        backgroundSize: 'cover',
        backgroundRepeat: "no-repeat",
        height: '60em'
    },    
    learnButtonHero: {
        ...theme.typography.learnButton,
        fontSize: "0.9rem",
        height: 45,
        width: 145
    },
    backgroundCTA: {
        backgroundImage: `url(${Background})`,
        backgroundAttachment: 'fixed',
        backgroundPosition: 'center',
        backgroundSize: 'cover',
        backgroundRepeat: "no-repeat",
        height: '60em',
        width: '100%',
        [theme.breakpoints.down("md")]: {
            backgroundImage:  `url(${MobileBackGround})`
        }
    },
    estimateButton: {
        ...theme.typography.estimate,
        borderRadius: 50,
        height: 80,
        width: 205,
        backgroundColor: theme.palette.common.orange,
        fontSize: '1.5rem',
        marginRight: '5em',
        marginLeft: '2em',
        "&:hover": {
            backgroundColor: theme.palette.secondary.light
        },
        [theme.breakpoints.down("xs")]: {
            marginRight: '0em',
            marginLeft: '0em',
            marginTop: '3em'
        }
    }
}));

export default function Contact(props) {
    const classes = useStyles();
    const theme = useTheme(); 
    const [name,setName] = useState('');
    const [email,setEmail] = useState('');
    const [phone,setPhone] = useState(''); 
    const [message, setMessage] = useState('');
    const matchesXS = useMediaQuery(theme.breakpoints.down("xs"));

    const onNameChangeHandler = (event) => {
        setName(event.target.value);
    }

    const onEmailChangeHandler = (event) => {
        setEmail(event.target.value);
    }

    const onPhoneChangeHandler = (event) => {
        setPhone(event.target.value);
    }

    const onMessageChangeHandler = (event) => {
        setMessage(event.target.value);
    }

    return(
        <Grid item container direction="row">
            <Grid item container direction="column" justify="center" lg={3}>
                <Grid item>
                    <Typography variant="h2" style={{lineHeight: 1}}>Contact Us</Typography>
                    <Typography variant="body1" style={{color: theme.palette.common.blue}}>We're waiting.</Typography>
                </Grid>
                <Grid item container>
                    <Grid item>
                        <img src={phoneIcon} alt="phone"style={{marginRight: '0.5em'}}/>
                    </Grid>
                    <Grid item>
                        <Typography variant="body1" style={{color: theme.palette.common.blue, fontSize: '1rem'}}>(555) 555-5555</Typography>
                    </Grid>
                </Grid>
                <Grid item container>
                    <Grid item>
                        <img src={emailIcon} alt="email"style={{marginRight: '0.5em', verticalAlign: "bottom"}}/>
                    </Grid>
                    <Grid item>
                        <Typography variant="body1" style={{color: theme.palette.common.blue, fontSize: '1rem'}}>tranminhtri9090@gmail.com</Typography>
                    </Grid>
                </Grid>
                <Grid item container>
                    <Grid item>
                        <TextField label="Name" id="Name" value={name} onChange={(e) => onNameChangeHandler(e)}/>
                    </Grid>
                    <Grid item>
                        <TextField label="Email" id="email" value={email} onChange={(e) => onEmailChangeHandler(e)}/>
                    </Grid>
                    <Grid item>
                        <TextField label="Phone" id="phone" value={phone} onChange={(e) => onPhoneChangeHandler(e)}/>
                    </Grid>
                </Grid>
                <Grid item>
                    <TextField value={message} multiline rows={10} id="message" onChange={(e) => onMessageChangeHandler(e)} />
                </Grid>
                <Grid item>
                    <Button variant="contained">
                        Send Message
                        <img src={airplane} alt="paper airplane" />
                    </Button>
                </Grid>
            </Grid>
            <Grid item container className={classes.background} lg={9}>
            <Grid item style={{marginLeft: matchesXS ? '0em' : '5em', textAlign: matchesXS ? "center" : "inherit"}}>
                <Grid container direction="column">
                    <Grid item>
                        <Typography variant="h2">Simple Software.<br /> Revolutionary Results.</Typography>
                        <Typography variant="subtitle2" style={{fontSize: '1.5rem'}}>Take advantage of the 21st Century.</Typography>
                        <Grid container justify={matchesXS ? "center" : undefined} item> 
                            <Button variant="outlined" className={classes.learnButtonHero} component={Link} to="/revolution">
                                <span style={{marginRight: 8}}>Learn More</span>
                                <ButtonArrow width={15} height={15} fill={theme.palette.common.blue} />
                            </Button>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
            <Grid item>
                <Button component={Link} to="/estimate" variant="contained" className={classes.estimateButton}>
                    Free Estimate
                </Button>
            </Grid>
            </Grid>
        </Grid>
    );
}
