import { createMuiTheme } from '@material-ui/core/styles';

const arcBlue = "#0B72B9";
const arcOrange = "#FFB860";
const arcGray = "#868686";
const arcWhite = "#ffffff";

export default createMuiTheme({
    palette: {
        common: {
            blue: `${arcBlue}`,
            orange: `${arcOrange}`,
            white: arcWhite
        },
        primary: {
            main: `${arcBlue}`
        },
        secondary: {
            main: `${arcOrange}`
        }
    },
    typography: {
        tab: {
            fontFamily: 'Raleway',
            textTransform: 'none',
            fontWeight: 700,
            fontSize: "1rem"
        },
        estimate: {
            fontFamily: 'Pacifico',
            fontSize: '1rem',
            textTransform: 'none',
            color: "white"
        },
        h2: {
            fontFamily: 'RaleWay',
            fontWeight: 700,
            fontSize: "2.5rem",
            color: arcBlue,
            lineHeight: 1.5
        },
        h3: {
            fontFamily: "Pacifico",
            fontSize: '2.5rem',
            color: arcBlue
        },
        h4: {
            fontFamily:  'Raleway',
            fontSize: '1.75rem',
            color: arcBlue,
            fontWeight: 700
        },
        subtitle2: {
            fontFamily:  'Raleway',
            fontSize: '1.5rem',
            color: arcWhite,
            fontWeight: 500
        },
        subtitle1: {
            color: arcGray,
            fontSize: '1.25rem',
            fontWeight: 300,
        },
        learnButton: {
            borderColor: arcBlue,
            color: arcBlue,
            borderWidth: 2,
            textTransform: 'none',
            borderRadius: 50,
            fontFamily: 'Roboto',
            fontWeight: 'bold'
        }
    }
});
