import React from 'react';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import ButtonArrow from '../original';
import Background from '../../assets/background.jpg';
import MobileBackGround from '../../assets/mobileBackground.jpg';

const useStyles = makeStyles(theme => ({
    learnButtonHero: {
        ...theme.typography.learnButton,
        fontSize: "0.9rem",
        height: 45,
        width: 145
    },
    backgroundCTA: {
        backgroundImage: `url(${Background})`,
        backgroundPosition: 'center',
        backgroundSize: 'cover',
        backgroundRepeat: "no-repeat",
        height: '100%',
        width: '100%',
        [theme.breakpoints.down("md")]: {
            backgroundImage:  `url(${MobileBackGround})`
        }
    }
}));

export default function CallToAction() {
    const classes = useStyles();
    const theme = useTheme();

    return(
        <Grid container style={{height: '60em'}} justify="flex-start" alignItems="center">    
            <Grid item style={{position: "absolute", marginLeft: '5em'}}>
                <Grid container direction="column">
                    <Grid item>
                        <Typography variant="h2">Simple Software.<br /> Revolutionary Results.</Typography>
                        <Typography variant="subtitle2" style={{fontSize: '1.5rem'}}>Take advantage of the 21st Century.</Typography>
                        <Button variant="outlined" className={classes.learnButtonHero}>
                            <span style={{marginRight: 8}}>Learn More</span>
                            <ButtonArrow width={15} height={15} fill={theme.palette.common.blue} />
                        </Button>
                    </Grid>
                </Grid>
            </Grid>
            <Grid item>
                <Button variant="contained" className={classes.estimateButton}>Free Estimate</Button>
            </Grid>
            <div className={classes.backgroundCTA}/>
        </Grid>
    );
}
