import React, {useState, useEffect} from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import {makeStyles} from '@material-ui/styles';
import logo from '../../assets/logo.svg';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Button from '@material-ui/core/Button';
import {Link} from 'react-router-dom';

// function take from navbar Material-UI
function ElevationScroll(props) {

    const { children } = props;

    const trigger = useScrollTrigger({
      disableHysteresis: true, // little delay for effect when user start scrolling and we want to disable it.
      threshold: 0, // Where the effect start, 0 when the user at the top of the page
    });

    return React.cloneElement(children, {
      elevation: trigger ? 4 : 0,
    });
}
  
const useStyles = makeStyles(theme => ({
    toolbarMargin: {
        ...theme.mixins.toolbar,
        marginBottom: "3em"
    },
    logo: {
        height: "8em"
    },
    tabContainer: {
        marginLeft: "auto"
    },
    Tab: {
        ...theme.typography.tab,
        minWidth: 10,
        marginLeft: '25px'
    },
    button: {
        ...theme.typography.estimate,
        borderRadius: '15px',
        marginLeft: '50px',
        marginRight: '25px',
        height: "45px"
    },
    logoContainer: {
        padding: '0px'
    }
}));

export default function Header(props) {
    const classes = useStyles();
    const [isActive,setIsActive] = useState(0);

    const onChangeHandler = (e, value) => {
        setIsActive(value);
    }

    useEffect(() => {
        if (window.location.pathname === "/" && isActive !== 0) {
            setIsActive(0); 
        } else if (window.location.pathname === "/services" && isActive !== 1) {
            setIsActive(1);
        } else if (window.location.pathname === "/revolution" && isActive !== 2) {
            setIsActive(2);
        } else if (window.location.pathname === "/about" && isActive !== 3) {
            setIsActive(3);
        } else if (window.location.pathname === "/contact" && isActive !== 4) {
            setIsActive(4);
        } else if (window.location.pathname === "/estimate" && isActive !== 5) {
            setIsActive(5);
        }
    }, [isActive]);

    return (
        <React.Fragment>
            <ElevationScroll>
            <AppBar>
                <Toolbar disableGutters>
                    <Button component={Link} to="/" className={classes.logoContainer} onClick={() => setIsActive(0)}>
                        <img src={logo} className={classes.logo} alt="Arc Logo"/>
                    </Button>
                    <Tabs value={isActive} onChange={onChangeHandler} indicatorColor="primary" className={classes.tabContainer}>
                        <Tab className={classes.Tab} component={Link} to="/" label="Home" />
                        <Tab className={classes.Tab} component={Link} to="/services" label="Services" />
                        <Tab className={classes.Tab} component={Link} to="/revolution" label="The Revolution" />
                        <Tab className={classes.Tab} component={Link} to="/about" label="About Us" />
                        <Tab className={classes.Tab} component={Link} to="/contact" label="Contact Us" />
                    </Tabs>
                    <Button variant="contained" color="secondary" component={Link} to="/estimate" className={classes.button}>Free Estimate</Button>
                </Toolbar>
            </AppBar>
            </ElevationScroll>
            <div className={classes.toolbarMargin} />
        </React.Fragment>
    );
}
