import React, {useState, useEffect} from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import {makeStyles} from '@material-ui/styles';
import logo from '../../assets/logo.svg';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Button from '@material-ui/core/Button';
import {Link} from 'react-router-dom';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import {useTheme} from '@material-ui/core/styles';

import Drawer from '@material-ui/core/Drawer';

const drawerWidth = 600;

// function take from navbar Material-UI
function ElevationScroll(props) {

    const { children } = props;

    const trigger = useScrollTrigger({
      disableHysteresis: true, // little delay for effect when user start scrolling and we want to disable it.
      threshold: 0, // Where the effect start, 0 when the user at the top of the page
    });

    return React.cloneElement(children, {
      elevation: trigger ? 4 : 0,
    });
}
  
const useStyles = makeStyles(theme => ({
    toolbarMargin: {
        ...theme.mixins.toolbar,
        marginBottom: "3em",
        [theme.breakpoints.down("md")]: {
            marginBottom: '2em'
        },
        [theme.breakpoints.down("xs")]: {
            marginBottom: '1em'
        }
    },
    logo: {
        height: "8em",
        [theme.breakpoints.down("md")]: {
            height: "7em"
        },
        [theme.breakpoints.down("xs")]: {
            height: "5em"
        }
    },
    tabContainer: {
        marginLeft: "auto"
    },
    Tab: {
        ...theme.typography.tab,
        minWidth: 10,
        marginLeft: '25px'
    },
    button: {
        ...theme.typography.estimate,
        borderRadius: '15px',
        marginLeft: '50px',
        marginRight: '25px',
        height: "45px"
    },
    logoContainer: {
        padding: '0px',
        "&:hover" : {
            backgroundColor: 'transparent'
        }
    },
    menu: {
        backgroundColor: theme.palette.common.blue,
        color: 'white',
        borderRadius: '0px'
    },
    menuItem: {
        ...theme.typography.tab,
        opacity: 0.7,
        "&:hover": {
            opacity: 1
        }
    },
    drawer: {
        width: drawerWidth,
        flexShrink: 0
    }, 
    drawerPaper: {
        width: drawerWidth
    }
}));

export default function Header(props) {
    const classes = useStyles(); // for Developer Styles 
    const theme = useTheme();
    const matches = useMediaQuery(theme.breakpoints.down("md"));
    const [isActive,setIsActive] = useState(0); // This control which tab is actived and add CSS to it
    const [anchorEl, setAnchorEl] = useState(null); // Control Which tab has the Menu Options
    const [open, setOpen] = useState(false); // Control "Open" and "Closed" status of Menu
    const [selectedIndex, setSelectedIndex] = useState(0); // Control which MenuItem inside Menu is actived and add CSS to it
    const [openDrawer, setOpenDrawer] = useState(false);

    const handlerDrawerOpen = () => {
        setOpenDrawer(true);
    }

    const handlerDrawerClose = () => {
        setOpenDrawer(false);
    }

    const onChangeHandler = (e, value) => {
        // Each tab in tabs has a value props 
        // On the tabs (tabs contain tab) also have a value props
        // If the value props in tabs equal value props to tab then that tab has the active classStyle 
        setIsActive(value);
    }

    const handlerClick = (e) => {
        // anchorEl props contain the menu itself 
        // combine anchorEl + open 
        // TH1: if open is false then anchorEl is equal to null
        // TH2: if open is true then anchorEl will be the menu 
        setAnchorEl(e.currentTarget);
        setOpen(true);
    }

    const handlerMenuClick = (e, index) => {
        setAnchorEl(null);
        setOpen(false);
        setSelectedIndex(index);
    }

    const handlerClose = (e) => {
        setAnchorEl(null);
        setOpen(false);
    }

    const menuOptions = [{name: "Services", link: "/services"}, 
                        {name: "Custome Sofware Development", link: '/customsofware'},
                        {name: "Mobile App Development", link: '/mobileapps'},
                        {name: "Web Development", link: '/websites'}];

    // useEffect is used to control the active class and keep it that way even if you refresh the page
    useEffect(() => {
        if (window.location.pathname === "/" && isActive !== 0) {
            setIsActive(0); 
        } else if (window.location.pathname === "/services" && isActive !== 1) {
            setIsActive(1);
        } else if (window.location.pathname === "/revolution" && isActive !== 2) {
            setIsActive(2);
        } else if (window.location.pathname === "/about" && isActive !== 3) {
            setIsActive(3);
        } else if (window.location.pathname === "/contact" && isActive !== 4) {
            setIsActive(4);
        } else if (window.location.pathname === "/estimate" && isActive !== 5) {
            setIsActive(5);
        } else if (window.location.pathname === "/customsofware" && isActive !== 1) {
            setIsActive(1);
            setSelectedIndex(1);
        } else if (window.location.pathname === "/mobileapps" && isActive !== 1) {
            setIsActive(1);
            setSelectedIndex(2);
        } else if (window.location.pathname === "/websites" && isActive !== 1) {
            setIsActive(1);
            setSelectedIndex(3);
        } 
    }, [isActive, selectedIndex]);

    const tabs = (
        <React.Fragment>
                    <Tabs value={isActive} onChange={onChangeHandler} indicatorColor="primary" className={classes.tabContainer}>
                        <Tab 
                            className={classes.Tab} 
                            component={Link} 
                            to="/" 
                            label="Home" />
                        <Tab 
                            aria-owns={anchorEl ? "simple-menu" : undefined}
                            aria-haspopup={anchorEl ? "true" : undefined}  
                            onMouseOver={handlerClick}
                            className={classes.Tab}
                            component={Link} 
                            to="/services" 
                            label="Services" />
                        <Tab 
                            className={classes.Tab} 
                            component={Link} 
                            to="/revolution" 
                            label="The Revolution" />
                        <Tab 
                            className={classes.Tab} 
                            component={Link}
                            to="/about" 
                            label="About Us" />
                        <Tab 
                            className={classes.Tab} 
                            component={Link} 
                            to="/contact" 
                            label="Contact Us" />
                    </Tabs>
                    <Button variant="contained" color="secondary" component={Link} to="/estimate" className={classes.button}>Free Estimate</Button>
                    <Menu 
                    id="simple-menu" 
                    anchorEl={anchorEl} 
                    open={open} 
                    onClose={handlerClose} 
                    classes={{paper: classes.menu}}
                    MenuListProps={{onMouseLeave: handlerClose}}
                    elevation={0}>
                        {menuOptions.map((option, index) => (
                            <MenuItem 
                                key = {option} 
                                component={Link} 
                                to={option.link}
                                classes={{root: classes.menuItem}}
                                onClick={(event) => {handlerMenuClick(event, index); setIsActive(1); handlerClose();}}
                                selected={index === selectedIndex && isActive === 1}>
                                {option.name}
                            </MenuItem>
                        ))}
                    </Menu>
        </React.Fragment>
    );

    const drawer = (
        <Drawer className={classes.drawer} variant="persistent" anchor="right" open={openDrawer} classes={{paper: classes.drawerPaper}}>
            Click me 
        </Drawer>
    );

    return (
        <React.Fragment>
            <ElevationScroll>
            <AppBar>
                <Toolbar disableGutters>
                    <Button component={Link} to="/" disableRipple className={classes.logoContainer} onClick={() => setIsActive(0)}>
                        <img src={logo} className={classes.logo} alt="Arc Logo"/>
                    </Button>
                    {matches ? drawer : tabs}

                </Toolbar>
            </AppBar>
            </ElevationScroll>
            <div className={classes.toolbarMargin} />
        </React.Fragment>
    );
}
