import React, {useState} from 'react';
import Lottie from 'react-lottie';
import {makeStyles, useTheme} from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import {cloneDeep} from 'lodash';
import check from '../assets/check.svg';
import send from '../assets/send.svg';
import software from '../assets/software.svg';
import mobile from '../assets/mobile.svg';
import website from '../assets/website.svg';
import backArrow from '../assets/backArrow.svg';
import forwardArrow from '../assets/forwardArrow.svg';
import backArrowDisabled from '../assets/backArrowDisabled.svg';
import forwardArrowDisabled from '../assets/forwardArrowDisabled.svg';
import camera from '../assets/camera.svg';
import upload from '../assets/upload.svg';
import person from '../assets/person.svg';
import persons from '../assets/persons.svg';
import info from '../assets/info.svg';
import bell from '../assets/bell.svg';
import users from '../assets/users.svg';
import iphone from '../assets/iphone.svg';
import gps from '../assets/gps.svg';
import customized from '../assets/customized.svg';
import data from '../assets/data.svg';
import android from '../assets/android.svg';
import globe from '../assets/globe.svg';
import biometrics from '../assets/biometrics.svg';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import estimateAnimation from '../animations/integrationAnimation/estimateAnimation/data.json';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import TextField from '@material-ui/core/TextField';

const useStyles = makeStyles((theme) => ({
    icon: {
        width: '12em',
        height: '10em'
    },
    estimateButton: {
        ...theme.typography.estimate,
        borderRadius: 50,
        backgroundColor: theme.palette.common.orange,
        height: 50,
        width: 225,
        fontSize: '1.25rem',
        marginTop: '3em',
        "&:hover": {
            backgroundColor: theme.palette.secondary.light
        }    
    },
    Message: {
      border: `2px solid ${theme.palette.common.blue}`,
      
      borderRadius: 5
    },
    specialText: {
      fontFamily: 'Raleway',
      fontWeight: 700,
      fontSize: '1.5rem',
      color: theme.palette.common.orange
    }
}));

const defaultQuestions = [
  {
    id: 1, 
    title: 'Which service are you interested in?', 
    active: true, 
    options: 
    [
      {
        id: 1, 
        title: "Custom Software Development", 
        subtitle: null, 
        icon: software, 
        iconAlt: 'three floating screen', 
        selected: false, 
        cost: 0},
      {
        id: 2, 
        title: "iOS/Android Development", 
        subtitle: null, 
        icon: mobile, 
        iconAlt: 'mobile icon', 
        selected: false, 
        cost: 0},
      {
        id: 3, 
        title: "Web Development", 
        subtitle: null, 
        icon: software, 
        iconAlt: 'web icon', 
        selected: false, 
        cost: 0
      }
    ] 
}];

const softwareQuestions = [
    { ...defaultQuestions[0], active: false },
    {
      id: 2,
      title: "Which platforms do you need supported?",
      subtitle: "Select all that apply.",
      options: [
        {
          id: 1,
          title: "Web Application",
          subtitle: null,
          icon: website,
          iconAlt: "computer outline",
          selected: false,
          cost: 100
        },
        {
          id: 2,
          title: "iOS Application",
          subtitle: null,
          icon: iphone,
          iconAlt: "outline of iphone",
          selected: false,
          cost: 100
        },
        {
          id: 3,
          title: "Android Application",
          subtitle: null,
          icon: android,
          iconAlt: "outlines of android phone",
          selected: false,
          cost: 100
        }
      ],
      active: true
    },
    {
      id: 3,
      title: "Which features do you expect to use?",
      subtitle: "Select all that apply.",
      options: [
        {
          id: 1,
          title: "Photo/Video",
          subtitle: null,
          icon: camera,
          iconAlt: "camera outline",
          selected: false,
          cost: 25
        },
        {
          id: 2,
          title: "GPS",
          subtitle: null,
          icon: gps,
          iconAlt: "gps pin",
          selected: false,
          cost: 25
        },
        {
          id: 3,
          title: "File Transfer",
          subtitle: null,
          icon: upload,
          iconAlt: "outline of cloud with arrow pointing up",
          selected: false,
          cost: 25
        }
      ],
      active: false
    },
    {
      id: 4,
      title: "Which features do you expect to use?",
      subtitle: "Select all that apply.",
      options: [
        {
          id: 1,
          title: "Users/Authentication",
          subtitle: null,
          icon: users,
          iconAlt: "outline of a person with a plus sign",
          selected: false,
          cost: 25
        },
        {
          id: 2,
          title: "Biometrics",
          subtitle: null,
          icon: biometrics,
          iconAlt: "fingerprint",
          selected: false,
          cost: 25
        },
        {
          id: 3,
          title: "Push Notifications",
          subtitle: null,
          icon: bell,
          iconAlt: "outline of a bell",
          selected: false,
          cost: 25
        }
      ],
      active: false
    },
    {
      id: 5,
      title: "What type of custom features do you expect to need?",
      subtitle: "Select one.",
      options: [
        {
          id: 1,
          title: "Low Complexity",
          subtitle: "(Informational)",
          icon: info,
          iconAlt: "'i' inside a circle",
          selected: false,
          cost: 25
        },
        {
          id: 2,
          title: "Medium Complexity",
          subtitle: "(Interactive, Customizable, Realtime)",
          icon: customized,
          iconAlt: "two toggle switches",
          selected: false,
          cost: 50
        },
        {
          id: 3,
          title: "High Complexity",
          subtitle: "(Data Modeling and Computation)",
          icon: data,
          iconAlt: "outline of line graph",
          selected: false,
          cost: 100
        }
      ],
      active: false
    },
    {
      id: 6,
      title: "How many users do you expect?",
      subtitle: "Select one.",
      options: [
        {
          id: 1,
          title: "0-10",
          subtitle: null,
          icon: person,
          iconAlt: "person outline",
          selected: false,
          cost: 1
        },
        {
          id: 2,
          title: "10-100",
          subtitle: null,
          icon: persons,
          iconAlt: "outline of two people",
          selected: false,
          cost: 1.25
        },
        {
          id: 3,
          title: "100+",
          subtitle: null,
          icon: person,
          iconAlt: "outline of three people",
          selected: false,
          cost: 1.5
        }
      ],
      active: false
    }
  ];
  
  
  const websiteQuestions = [
    { ...defaultQuestions[0], active: false },
    {
      id: 2,
      title: "Which type of website are you wanting?",
      subtitle: "Select one.",
      options: [
        {
          id: 1,
          title: "Basic",
          subtitle: "(Informational)",
          icon: info,
          iconAlt: "person outline",
          selected: false,
          cost: 100
        },
        {
          id: 2,
          title: "Interactive",
          subtitle: "(Users, API's, Messaging)",
          icon: customized,
          iconAlt: "outline of two people",
          selected: false,
          cost: 200
        },
        {
          id: 3,
          title: "E-Commerce",
          subtitle: "(Sales)",
          icon: globe,
          iconAlt: "outline of three people",
          selected: false,
          cost: 250
        }
      ],
      active: true
    }
  ];
  

export default function Estimate() {
    const classes = useStyles();
    const theme = useTheme();

    const matchesSM = useMediaQuery(theme.breakpoints.down("sm"));

    const [questions, setQuestions] = useState(defaultQuestions);
    const [dialogOpen, setDialogOpen] = useState(false);
    const [name,setName] = useState('');

    const [email,setEmail] = useState('');
    const [emailHelper,setEmailHelper] = useState("");

    const [phone,setPhone] = useState(''); 
    const [phoneHelper,setPhoneHelper] = useState("");

    const [message, setMessage] = useState('');
    const [total, setTotal] = useState(0);

    const [services, setServices] = useState([]);
    const [platforms, setPlatform] = useState([]);
    const [features, setFeatures] = useState([]);
    const [customFeatures, setCustomFeatures] = useState("");
    const [category, setCategory] = useState("");
    const [users, setUsers] = useState(""); 

    const defaultOptions = {
        loop: true,
        autoplay: true,
        animationData: estimateAnimation,
        renderSetting:  {
            preserveAspectRatio: 'xMidYMid slice'
        }
    }

    const nextQuestion = () => {
      const newQuestions = cloneDeep(questions);
      const currentActived = newQuestions.filter(question => question.active);
      const activedIndex = currentActived[0].id - 1;
      const nextIndex =  activedIndex + 1;
      newQuestions[activedIndex] = {...currentActived[0], active: false}
      newQuestions[nextIndex] = {...newQuestions[nextIndex], active: true} 
      setQuestions(newQuestions);
    } 

    const prevQuestion = () => {
      const newQuestions = cloneDeep(questions);
      const currentActived = newQuestions.filter(question => question.active);
      const activedIndex = currentActived[0].id - 1;
      const nextIndex =  activedIndex - 1;
      newQuestions[activedIndex] = {...currentActived[0], active: false}
      newQuestions[nextIndex] = {...newQuestions[nextIndex], active: true} 
      setQuestions(newQuestions);
    }

    const handlerSelect = (id) => {
      const newQuestions = cloneDeep(questions);
      const currentActive = newQuestions.filter(question => question.active);
      const activeIndex = currentActive[0].id - 1;

      const newSelected = newQuestions[activeIndex].options[id-1];
      const prevSelected = currentActive[0].options.filter(option => option.selected);

      switch(currentActive[0].subtitle) {
        case'Select one.':
          if (prevSelected[0]) {
            prevSelected[0].selected = !prevSelected[0].selected;
          } 
          newSelected.selected = !newSelected.selected;
          break;
        default: 
          newSelected.selected = !newSelected.selected;
          break;
      }

      switch(newSelected.title) {
        case 'Custom Software Development': 
          setQuestions(softwareQuestions);
          setServices(newSelected.title);
          break;
        case 'iOS/Android Development':
          setQuestions(softwareQuestions);
          setServices(newSelected.title);
          break;
        case 'Web Development':
          setQuestions(websiteQuestions);
          setServices(newSelected.title);
          break;
        default: 
          setQuestions(newQuestions);
      }
    }

    const onChange = event => {
      let valid;

      switch(event.target.id) {
          case'email':
              setEmail(event.target.value);
              valid = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/.test(event.target.value);
              if (!valid) {
                  setEmailHelper("Invalid Email");
              } else {
                  setEmailHelper("");
              }

              break;
          case('phone'): 
              setPhone(event.target.value);
              valid = /^\(?([0-9]{3})\)?[-. ]?([0-9]{3})[-. ]?([0-9]{4})$/.test(event.target.value);
              if (!valid) {
                  setPhoneHelper("Invalid PhoneNumber");
              } else {
                  setPhoneHelper('');
              }
              break;
          default: 
              break;
      }
    }

    const onNameChangeHandler = (event) => {
      setName(event.target.value);
    }

    const onMessageChangeHandler = (event) => {
      setMessage(event.target.value);
    }

    const navigationPreviousDisabled = () => {
      const currentActive = questions.filter(question => question.active);
      if (currentActive[0].id === 1) { 
        return true;
      } else {
        return false;
      }
    }

    const navigationForwardDisabled = () => {
      const currentActive = questions.filter(question => question.active);
      if (currentActive[0].id === questions[questions.length-1].id) { 
        return true;
      } else {
        return false;
      }
    }

    const getTotal = () => {
      let cost = 0;
      const selections = questions.map(question => question.options.filter(option => option.selected)).filter(question => question.length > 0);
      selections.map(options => options.map(option => cost += option.cost));
      if (questions.length > 2) {
        const userCost = questions.filter(question => question.title === 'How many users do you expect?').map(question => question.options.filter(option => option.selected))[0][0];
        
        setUsers(userCost.title);
        cost -= userCost.cost;
        cost *= userCost.cost;
      }
      setTotal(cost); 
    }

    const getPlatform = () => {
      let newPlatforms = [];
      if (questions.length > 2) { 
       questions.filter(question => question.title === 'Which platforms do you need supported?')
                .map(question => question.options.filter(option => option.selected))[0].map(option => newPlatforms.push(option.title));
      console.log(newPlatforms);
      setPlatform(newPlatforms);
      }
    }

    const getFeatures = () => {
      let newFeatures = [];
      if (questions.length > 2) {
        questions.filter(question => question.title === "Which features do you expect to use?")
                .map(question => question.options.filter(option => option.selected))
                .map(option => option.map(newFeature => newFeatures.push(newFeature.title)));
      console.log(newFeatures);
      setFeatures(newFeatures);
      }
    }

    const getCustomFeatures = () => {
      if (questions.length > 2) {
        const newCustomFeatures = questions.filter(question => question.title === 'What type of custom features do you expect to need?')
                                        .map(question => question.options.filter(option => option.selected))[0][0].title;
        setCustomFeatures(newCustomFeatures);
      }
    }

    return(
        <Grid container direction="row">
            
            <Grid item container direction="column" lg>
                <Grid item style={{marginTop: '2em', marginLeft: '5em'}}>
                    <Typography variant="h2">Estimate</Typography>
                </Grid>
                <Grid item style={{marginRight: '10em', maxWidth: '50em', marginTop: '5.5em'}}>
                    <Lottie options={defaultOptions} height="100%" width="100%"/>
                </Grid>
            </Grid>

            <Grid item container direction="column" alignItems="center" lg style={{marginRight: '2em', marginBottom: '25em'}}>
                {questions.filter(question => question.active).map( (question, index) => (
                    <React.Fragment key={index}>
                        <Grid item>
                            <Typography variant="h2" align="center" 
                                style={{fontWeight: 500, fontSize: "2.25em", marginTop: '1em', lineHeight: 1.25}}>
                                    {question.title}
                            </Typography>
                            <Typography variant="body1" align='center' style={{marginBottom: '2.5em'}} gutterBottom>
                                    {question.subtitle}
                            </Typography>
                        </Grid>
                        <Grid item container>
                            {question.options.map((option) => (
                              <Grid 
                                item container 
                                direction="column" 
                                md key={option.id} 
                                component={Button}
                                onClick={() => handlerSelect(option.id)}
                                style={{display: 'grid', textTransform: 'none', borderRadius: 0,backgroundColor: option.selected ? theme.palette.secondary.main : undefined}}>
                                <Grid item style={{maxWidth: '14em'}}>
                                    <Typography variant="h6" align="center" style={{marginBottom: "1em"}}>
                                        {option.title}
                                    </Typography>
                                    <Typography variant="caption" align="center">
                                        {option.subtitle}
                                    </Typography>   
                                </Grid>
                                <Grid item>
                                    <img src={option.icon} alt={option.iconAlt} className={classes.icon}/>
                                </Grid>
                            </Grid>
                              ))}
                        </Grid>
                    </React.Fragment>
                ))}

                <Grid item container justify="space-between" style={{width: '15em', marginTop: '3em'}}>
                    <Grid item>
                      <IconButton disabled={navigationPreviousDisabled()} onClick={prevQuestion}>
                        <img src={navigationPreviousDisabled() ? backArrowDisabled : backArrow} alt="Back Arrow" />
                      </IconButton>
                    </Grid>
                    <Grid item>
                      <IconButton disabled={navigationForwardDisabled()} onClick={nextQuestion}>
                        <img src={navigationForwardDisabled() ? forwardArrowDisabled : forwardArrow} alt="Forward Arrow" />
                      </IconButton>
                    </Grid>
                </Grid>

                <Grid item>
                    <Button 
                      variant="contained" 
                      onClick={() => {
                          setDialogOpen(true); 
                          getTotal(); 
                          getPlatform(); 
                          getFeatures();
                          getCustomFeatures();
                      }} className={classes.estimateButton}>
                      Get Estimate
                    </Button>
                </Grid>

            </Grid>
            <Dialog open={dialogOpen} onClose={() => setDialogOpen(false)}>
                <Grid container justify="center">
                  <Grid item>
                      <Typography variant="h2" align="center">
                        Estimate
                      </Typography>
                  </Grid>
                </Grid>
                <DialogContent>
                  <Grid container>
                    <Grid item container direction="column" md={7}>
                          <Grid item style={{marginBottom: '0.5em'}}>
                                <TextField 
                                label="Name" 
                                fullWidth 
                                id="Name" 
                                value={name} 
                                onChange={(e) => onNameChangeHandler(e)}/>
                            </Grid>
                            <Grid item style={{marginBottom: '0.5em'}}>
                                <TextField 
                                label="Email" 
                                error={emailHelper.length !== 0} 
                                helperText = {emailHelper}
                                fullWidth id="email" 
                                value={email} 
                                onChange={(e) => onChange(e)}/>
                            </Grid>
                            <Grid item style={{marginBottom: '0.5em'}}>
                            <TextField 
                                label="Phone" 
                                error={phoneHelper.length !== 0} 
                                helperText = {phoneHelper}
                                fullWidth id="phone" 
                                value={phone} 
                                onChange={(e) => onChange(e)}/>
                            </Grid>
                            <Grid item style={{maxWidth: matchesSM ? '100%' : "20em"}}>
                              <TextField value={message} multiline rows={10} 
                              id="message" onChange={(e) => onMessageChangeHandler(e)} 
                              className={classes.Message} fullWidth
                              InputProps={{disableUnderline: true}}
                              style={{marginTop: '2em'}}/>
                            </Grid>
                            <Grid item>
                              <Typography variant="body1" paragraph>
                                We can create this digital solution for an estimated : <span className={classes.specialText}>${total.toFixed(2)}</span>
                              </Typography>
                              <Typography variant="body1" paragraph>
                                Fill out your name, phone number and email, place your request, and we'll get back to you with details moving forward and a final price.
                              </Typography>
                            </Grid>
                        </Grid>
                        <Grid item container direction="column" md={5}>
                          <Grid item>

                            <Grid container direction="column">
                              <Grid item container alignItems="center">
                                <Grid item>
                                  <img src={check} alt="checkmark" />
                                </Grid>
                                <Grid item>
                                  <Typography variant="body1">
                                    You want : {services} for
                                    {platforms.length > 0 ? `for ${
                              //if only web application is selected...
                              platforms.indexOf("Web Application") > -1 &&
                              platforms.length === 1
                                ? //then finish sentence here
                                  "a Web Application."
                                : //otherwise, if web application and another platform is selected...
                                platforms.indexOf("Web Application") > -1 &&
                                  platforms.length === 2
                                ? //then finish the sentence here
                                  `a Web Application and an ${platforms[1]}.`
                                : //otherwise, if only one platform is selected which isn't web application...
                                platforms.length === 1
                                ? //then finish the sentence here
                                  `an ${platforms[0]}`
                                : //otherwise, if other two options are selected...
                                platforms.length === 2
                                ? //then finish the sentence here
                                  "an iOS Application and an Android Application."
                                : //otherwise if all three are selected...
                                platforms.length === 3
                                ? //then finish the sentence here
                                  "a Web Application, an iOS Application, and an Android Application."
                                : null
                            }` : null}
                                  </Typography>
                                </Grid>
                              </Grid>

                              <Grid item container alignItems="center">
                                <Grid item>
                                  <img src={check} alt="checkmark" />
                                </Grid>
                                <Grid item>
                                  <Typography variant="body1">
                                  {"with "}
                        {/* if we have features... */}
                        {features.length > 0
                          ? //...and there's only 1...
                            features.length === 1
                              ? //then end the sentence here
                              `${features[0]}.`
                              : //otherwise, if there are two features...
                            features.length === 2
                              ? //...then end the sentence here
                              `${features[0]} and ${features[1]}.`
                              : //otherwise, if there are three or more features...
                              features
                            //filter out the very last feature...
                            .filter(
                              (feature, index) =>
                              index !== features.length - 1
                            )
                            //and for those features return their name...
                            .map((feature, index) => (
                              <span key={index}>{`${feature}, `}</span>
                            ))
                          : null}
                        {features.length > 0 &&
                          features.length !== 1 &&
                          features.length !== 2
                            ? //...and then finally add the last feature with 'and' in front of it
                            ` and ${features[features.length - 1]}.`
                            : null}
                                  </Typography>
                                </Grid>
                              </Grid>

                              <Grid item container alignItems="center">
                                <Grid item>
                                  <img src={check} alt="checkmark" />
                                </Grid>
                                <Grid item>
                                  <Typography variant="body1">
                                    The custom features will be of {customFeatures.toLowerCase()} {`, and the project will be used by about ${users} users.`}
                                  </Typography>
                                </Grid>
                              </Grid>

                            </Grid>
                          </Grid>

                          <Grid item>
                            <Button variant="contained" className={classes.estimateButton}>
                              Place Request
                              <img src={send} alt="paper airplane" style={{marginLeft: '0.5em'}} />  
                            </Button>
                          </Grid>
                        </Grid>
                  </Grid>
                </DialogContent>
            </Dialog>
        </Grid>
    );
}
